definition.script:
pipeline {
    agent any 

    parameters {
        booleanParam(name: 'SKIP_PARALLEL_STAGE', defaultValue: false, description: 'Skip the parallel execution stage')
    }

    stages {
        stage('Stage 1: Preparation') {
            steps {
                echo 'Preparing...'
                // Add preparation steps here
            }
        }

        stage('Stage 2: Parallel Execution') {
            when {
                expression { !params.SKIP_PARALLEL_STAGE }
            }
            parallel {
                stage('Parallel Job 1') {
                    steps {
                        echo 'Running Parallel Job 1...'
                        // Add steps for parallel job 1 here
                    }
                }
                stage('Parallel Job 2') {
                    steps {
                        echo 'Running Parallel Job 2...'
                        // Add steps for parallel job 2 here
                    }
                }
            }
        }

        stage('Stage 3: Cleanup') {
            steps {
                echo 'Cleaning up...'
                // Add cleanup steps here
            }
        }
    }
}
